import { ResponsiveBar } from '@nivo/bar'

// make sure parent container have a defined height when using
// responsive component, otherwise height will be 0 and
// no chart will be rendered.
// website examples showcase many properties,
// you'll often use just a few of them.


const data = [
    {
      "day": "Mon",
      "sales": 80,
      "salesColor": "hsl(46, 70%, 50%)",
      "purchases": 142,
      "purchasesColor": "hsl(289, 70%, 50%)",
    },
    {
      "day": "Tue",
      "hot dog": 80,
      "purchases Color": "hsl(211, 70%, 50%)",
      "sales": 98,
      "burgerColor": "hsl(289, 70%, 50%)",
    },
    {
      "day": "Wed",
      "hot dog": 48,
      "purchases Color": "hsl(355, 70%, 50%)",
      "sales": 24,
      "burgerColor": "hsl(234, 70%, 50%)",
    },
    {
      "day": "Fri",
      "hot dog": 28,
      "purchases Color": "hsl(244, 70%, 50%)",
      "sales": 87,
      "burgerColor": "hsl(218, 70%, 50%)",
    },
    {
      "day": "Sat",
      "hot dog": 73,
      "purchases Color": "hsl(41, 70%, 50%)",
      "sales": 32,
      "burgerColor": "hsl(251, 70%, 50%)",
    },
    {
      "day": "Sun",
      "hot dog": 162,
      "purchases Color": "hsl(241, 70%, 50%)",
      "sales": 100,
      "burgerColor": "hsl(157, 70%, 50%)",
    }
  ]


const Barchartt = () => {
    return(
        <ResponsiveBar
            data={data}
            keys={[
                'hot dog',
                'sales',
            ]}
            indexBy="day"
            margin={{ top: 10, right: 130, bottom: 10, left: 60 }}
            padding={0.2}
            innerPadding={5}
            groupMode="grouped"
            valueScale={{ type: 'linear' }}
            indexScale={{ type: 'band', round: true }}
            colors={{ scheme: 'nivo' }}
            defs={[
                {
                    id: 'dots',
                    type: 'patternDots',
                    background: 'inherit',
                    color: '#38bcb2',
                    size: 4,
                    padding: 1,
                    stagger: true
                },
                {
                    id: 'lines',
                    type: 'patternLines',
                    background: 'inherit',
                    color: '#eed312',
                    rotation: -45,
                    lineWidth: 6,
                    spacing: 10
                }
            ]}
            fill={[
                {
                    match: {
                        id: 'fries'
                    },
                    id: 'dots'
                },
                {
                    match: {
                        id: 'sandwich'
                    },
                    id: 'lines'
                }
            ]}
            borderRadius={8}
            borderColor={{
                from: 'color',
                modifiers: [
                    [
                        'darker',
                        1.6
                    ]
                ]
            }}
            axisTop={null}
            axisRight={null}
            axisBottom={{
                tickSize: 5,
                tickPadding: 5,
                tickRotation: 0,
                legend: 'country',
                legendPosition: 'middle',
                legendOffset: 32
            }}
            axisLeft={{
                tickSize: 5,
                tickPadding: 5,
                tickRotation: 0,
                legend: 'food',
                legendPosition: 'middle',
                legendOffset: -40
            }}
            enableLabel={false}
            labelTextColor={{
                from: 'color',
                modifiers: [
                    [
                        'darker',
                        1.6
                    ]
                ]
            }}
            legends={[
                {
                    dataFrom: 'keys',
                    anchor: 'bottom-right',
                    direction: 'column',
                    justify: false,
                    translateX: 120,
                    translateY: 0,
                    itemsSpacing: 2,
                    itemWidth: 100,
                    itemHeight: 20,
                    itemDirection: 'left-to-right',
                    itemOpacity: 0.85,
                    symbolSize: 20,
                    effects: [
                        {
                            on: 'hover',
                            style: {
                                itemOpacity: 1
                            }
                        }
                    ]
                }
            ]}
            role="application"
            ariaLabel="Nivo bar chart demo"
            barAriaLabel={function(e){return e.id+": "+e.formattedValue+" in country: "+e.indexValue}}
        />
    )
    }

export default Barchartt
