import { useMemo } from 'react'
import { scaleSequential } from 'd3-scale'
import { colorInterpolators, ColorInterpolatorId } from '../schemes'

export interface SequentialColorScaleBaseConfig {
    type: 'sequential'
    minValue?: number
    maxValue?: number
}

export interface SequentialColorScaleSchemeConfig extends SequentialColorScaleBaseConfig {
    scheme?: ColorInterpolatorId
}

export interface SequentialColorScaleColorsConfig extends SequentialColorScaleBaseConfig {
    colors: [string, string]
}

export interface SequentialColorScaleInterpolatorConfig extends SequentialColorScaleBaseConfig {
    interpolator: (t: number) => string
}

export type SequentialColorScaleConfig =
    | SequentialColorScaleSchemeConfig
    | SequentialColorScaleColorsConfig
    | SequentialColorScaleInterpolatorConfig

export interface SequentialColorScaleValues {
    min: number
    max: number
}

export const sequentialColorScaleDefaults: {
    scheme: ColorInterpolatorId
} = {
    scheme: 'turbo',
}

export const getSequentialColorScale = (
    config: SequentialColorScaleConfig,
    values: SequentialColorScaleValues
) => {
    const { minValue, maxValue } = config
    const min = minValue !== undefined ? minValue : values.min
    const max = maxValue !== undefined ? maxValue : values.max

    const colorScale = scaleSequential<string>().domain([min, max]).clamp(true)
    if ('colors' in config) {
        colorScale.range(config.colors)
    } else if ('interpolator' in config) {
        colorScale.interpolator(config.interpolator)
    } else {
        const scheme = config.scheme ?? sequentialColorScaleDefaults.scheme
        colorScale.interpolator(colorInterpolators[scheme])
    }

    return colorScale
}

export const useSequentialColorScale = (
    config: SequentialColorScaleConfig,
    values: SequentialColorScaleValues
) => useMemo(() => getSequentialColorScale(config, values), [config, values])
import { useMemo } from 'react'
import { scaleDiverging } from 'd3-scale'
import { colorInterpolators, ColorInterpolatorId } from '../schemes'

interface DivergingColorScaleBaseConfig {
    type: 'diverging'
    minValue?: number
    maxValue?: number
    divergeAt?: number
}

export interface DivergingColorScaleSchemeConfig extends DivergingColorScaleBaseConfig {
    scheme?: ColorInterpolatorId
}

export interface DivergingColorScaleColorsConfig extends DivergingColorScaleBaseConfig {
    colors: [string, string, string]
}

export interface DivergingColorScaleInterpolatorConfig extends DivergingColorScaleBaseConfig {
    interpolator: (t: number) => string
}

export type DivergingColorScaleConfig =
    | DivergingColorScaleSchemeConfig
    | DivergingColorScaleColorsConfig
    | DivergingColorScaleInterpolatorConfig

export interface DivergingColorScaleValues {
    min: number
    max: number
}

export const divergingColorScaleDefaults: {
    scheme: ColorInterpolatorId
    divergeAt: number
} = {
    scheme: 'red_yellow_blue',
    divergeAt: 0.5,
}

export const getDivergingColorScale = (
    config: DivergingColorScaleConfig,
    values: DivergingColorScaleValues
) => {
    const { minValue, maxValue } = config
    const min = minValue !== undefined ? minValue : values.min
    const max = maxValue !== undefined ? maxValue : values.max
    const domain = [min, min + (max - min) / 2, max]

    const divergeAt = config.divergeAt ?? divergingColorScaleDefaults.divergeAt
    const offset = 0.5 - divergeAt

    const colorScale = scaleDiverging<string>().domain(domain).clamp(true)
    let interpolator = (t: number) => String(t) as string
    if ('colors' in config) {
        interpolator = scaleDiverging<string>()
            .domain(domain.map(x => x - offset * (max - min)))
            .range(config.colors)
            .interpolator()
    } else if ('interpolator' in config) {
        interpolator = config.interpolator
    } else {
        const scheme = config.scheme ?? divergingColorScaleDefaults.scheme
        interpolator = colorInterpolators[scheme]
    }
    const offsetInterpolator = (t: number) => interpolator(t + offset)
    return colorScale.interpolator(offsetInterpolator)
}

export const useDivergingColorScale = (
    config: DivergingColorScaleConfig,
    values: DivergingColorScaleValues
) => useMemo(() => getDivergingColorScale(config, values), [config, values])
import { useMemo } from 'react'
import { scaleQuantize } from 'd3-scale'
import { colorInterpolators, ColorInterpolatorId } from '../schemes'

// colors from a scheme
export interface QuantizeColorScaleSchemeConfig {
    type: 'quantize'
    domain?: [number, number]
    scheme?: ColorInterpolatorId
    steps?: number
}

// explicit colors
export interface QuantizeColorScaleColorsConfig {
    type: 'quantize'
    domain?: [number, number]
    colors: string[]
}

export type QuantizeColorScaleConfig =
    | QuantizeColorScaleSchemeConfig
    | QuantizeColorScaleColorsConfig

export interface QuantizeColorScaleValues {
    min: number
    max: number
}

export const quantizeColorScaleDefaults: {
    scheme: ColorInterpolatorId
    steps: NonNullable<QuantizeColorScaleSchemeConfig['steps']>
} = {
    scheme: 'turbo',
    steps: 7,
}

export const getQuantizeColorScale = (
    config: QuantizeColorScaleConfig,
    values: QuantizeColorScaleValues
) => {
    const colorScale = scaleQuantize<string>()
        .domain(config.domain || [values.min, values.max])
        .nice()

    if ('colors' in config) {
        colorScale.range(config.colors)
    } else {
        const scheme = config.scheme || quantizeColorScaleDefaults.scheme
        const steps = config.steps === undefined ? quantizeColorScaleDefaults.steps : config.steps
        const interpolator = colorInterpolators[scheme]
        const colors = Array.from({ length: steps }).map((_, step) =>
            interpolator(step * (1 / (steps - 1)))
        )

        colorScale.range(colors)
    }

    return colorScale
}

export const useQuantizeColorScale = (
    config: QuantizeColorScaleConfig,
    values: QuantizeColorScaleValues
) => useMemo(() => getQuantizeColorScale(config, values), [config, values])
import { useMemo } from 'react'
import { ScaleDiverging, ScaleQuantize, ScaleSequential, scaleLinear } from 'd3-scale'
import {
    SequentialColorScaleConfig,
    SequentialColorScaleValues,
    getSequentialColorScale,
} from './sequentialColorScale'
import {
    DivergingColorScaleConfig,
    DivergingColorScaleValues,
    getDivergingColorScale,
} from './divergingColorScale'
import {
    QuantizeColorScaleConfig,
    QuantizeColorScaleValues,
    getQuantizeColorScale,
} from './quantizeColorScale'

export type ContinuousColorScaleConfig =
    | SequentialColorScaleConfig
    | DivergingColorScaleConfig
    | QuantizeColorScaleConfig

export type ContinuousColorScaleValues =
    | SequentialColorScaleValues
    | DivergingColorScaleValues
    | QuantizeColorScaleValues

const isSequentialColorScaleConfig = (
    config: ContinuousColorScaleConfig
): config is SequentialColorScaleConfig => config.type === 'sequential'

const isDivergingColorScaleConfig = (
    config: ContinuousColorScaleConfig
): config is DivergingColorScaleConfig => config.type === 'diverging'

const isQuantizeColorScaleConfig = (
    config: ContinuousColorScaleConfig
): config is QuantizeColorScaleConfig => config.type === 'quantize'

export const getContinuousColorScale = <Config extends ContinuousColorScaleConfig>(
    config: Config,
    values: ContinuousColorScaleValues
) => {
    if (isSequentialColorScaleConfig(config)) {
        return getSequentialColorScale(config, values)
    }

    if (isDivergingColorScaleConfig(config)) {
        return getDivergingColorScale(config, values)
    }

    if (isQuantizeColorScaleConfig(config)) {
        return getQuantizeColorScale(config, values)
    }

    throw new Error('Invalid continuous color scale config')
}

export const useContinuousColorScale = (
    config: ContinuousColorScaleConfig,
    values: ContinuousColorScaleValues
) => useMemo(() => getContinuousColorScale(config, values), [config, values])

export const computeContinuousColorScaleColorStops = (
    scale: ScaleSequential<string> | ScaleDiverging<string> | ScaleQuantize<string>,
    steps = 16
) => {
    const domain = scale.domain()

    // quantize
    if ('thresholds' in scale) {
        const stops: {
            key: string
            offset: number
            stopColor: string
        }[] = []

        const normalizedScale = scaleLinear().domain(domain).range([0, 1])
        scale.range().forEach((color, index) => {
            const [start, end] = scale.invertExtent(color)

            stops.push({
                key: `${index}.0`,
                offset: normalizedScale(start),
                stopColor: color,
            })
            stops.push({
                key: `${index}.1`,
                offset: normalizedScale(end),
                stopColor: color,
            })
        })

        return stops
    }

    const colorStopsScale = scale.copy()
    if (domain.length === 2) {
        // sequential
        colorStopsScale.domain([0, 1])
    } else if (domain.length === 3) {
        // diverging
        colorStopsScale.domain([0, 0.5, 1])
    }

    return ((colorStopsScale as any).ticks(steps) as number[]).map((value: number) => ({
        key: `${value}`,
        offset: value,
        stopColor: `${colorStopsScale(value)}`,
    }))
}
import { useMemo } from 'react'
import { get, isPlainObject } from 'lodash'
import { scaleOrdinal } from 'd3-scale'
import {
    ColorSchemeId,
    colorSchemes,
    isCategoricalColorScheme,
    isSequentialColorScheme,
    isDivergingColorScheme,
} from '../schemes'

/**
 * Static color.
 */
export type OrdinalColorScaleConfigStaticColor = string

/**
 * User defined function, receiving the current datum.
 */
export type OrdinalColorScaleConfigCustomFunction<Datum> = (d: Datum) => string

/**
 * Pre-defined color scheme.
 */
export interface OrdinalColorScaleConfigScheme {
    scheme: ColorSchemeId
    // size is useful for diverging & sequential colors,
    // as they are array of array, whereas categorical colors
    // are simple arrays, if the size isn't specified,
    // the bigger array will be selected, this means the 11th
    // for diverging colors and 9th for sequential ones.
    size?: number
}

/**
 * User defined colors.
 */
export type OrdinalColorScaleConfigCustomColors = string[]

/**
 * Get color from datum.
 */
export interface OrdinalColorScaleConfigDatumProperty {
    // path to the color property
    datum: string
}

export type OrdinalColorScaleConfig<Datum = any> =
    | OrdinalColorScaleConfigStaticColor
    | OrdinalColorScaleConfigCustomFunction<Datum>
    | OrdinalColorScaleConfigScheme
    | OrdinalColorScaleConfigCustomColors
    | OrdinalColorScaleConfigDatumProperty

const isOrdinalColorScaleConfigScheme = <Datum>(
    config: OrdinalColorScaleConfig<Datum>
): config is OrdinalColorScaleConfigScheme => {
    return (config as OrdinalColorScaleConfigScheme).scheme !== undefined
}

const isOrdinalColorScaleConfigDatumProperty = <Datum>(
    config: OrdinalColorScaleConfig<Datum>
): config is OrdinalColorScaleConfigDatumProperty => {
    return (config as OrdinalColorScaleConfigDatumProperty).datum !== undefined
}

export type DatumIdentityAccessor<Datum> = (datum: Datum) => string | number

export type OrdinalColorScale<Datum> = (d: Datum) => string

/**
 * Compute an ordinal color scale
 */
export const getOrdinalColorScale = <Datum = any>(
    config: OrdinalColorScaleConfig<Datum>,
    identity?: string | DatumIdentityAccessor<Datum>
): OrdinalColorScale<Datum> => {
    // user defined function
    if (typeof config === 'function') {
        return config
    }

    // compute accessor to the datum identity
    const getIdentity =
        typeof identity === 'function' ? identity : (datum: Datum) => get(datum, identity as string)

    // user defined color array
    if (Array.isArray(config)) {
        const scale = scaleOrdinal(config)
        const generator = (datum: Datum) => scale(getIdentity(datum))
        generator.scale = scale

        return generator as OrdinalColorScale<Datum>
    }

    if (isPlainObject(config)) {
        // use color from current datum
        if (isOrdinalColorScaleConfigDatumProperty(config)) {
            return (datum: Datum) => get(datum, config.datum)
        }

        // ordinal scale from predefined scheme
        if (isOrdinalColorScaleConfigScheme(config)) {
            // categorical color scheme
            if (isCategoricalColorScheme(config.scheme)) {
                const scale = scaleOrdinal(colorSchemes[config.scheme])
                const generator = (datum: Datum) => scale(getIdentity(datum))
                generator.scale = scale

                return generator as OrdinalColorScale<Datum>
            }

            // Diverging color schemes support a size k ranging from 3 to 11
            if (isDivergingColorScheme(config.scheme)) {
                if (config.size !== undefined && (config.size < 3 || config.size > 11)) {
                    throw new Error(
                        `Invalid size '${config.size}' for diverging color scheme '${config.scheme}', must be between 3~11`
                    )
                }

                const scale = scaleOrdinal(colorSchemes[config.scheme][config.size || 11])
                const generator = (d: Datum) => scale(getIdentity(d))
                generator.scale = scale

                return generator as OrdinalColorScale<Datum>
            }

            // Sequential, single-hue color schemes support a size k ranging from 3 to 9.
            // Sequential, multi-hue color schemes support a size k ranging from 3 to 9.
            if (isSequentialColorScheme(config.scheme)) {
                if (config.size !== undefined && (config.size < 3 || config.size > 9)) {
                    throw new Error(
                        `Invalid size '${config.size}' for sequential color scheme '${config.scheme}', must be between 3~9`
                    )
                }

                const scale = scaleOrdinal(colorSchemes[config.scheme][config.size || 9])
                const generator = (d: Datum) => scale(getIdentity(d))
                generator.scale = scale

                return generator as OrdinalColorScale<Datum>
            }
        }

        throw new Error(
            `Invalid colors, when using an object, you should either pass a 'datum' or a 'scheme' property`
        )
    }

    // static color
    return () => config as string
}

export const useOrdinalColorScale = <Datum = any>(
    config: OrdinalColorScaleConfig<Datum>,
    identity: string | DatumIdentityAccessor<Datum>
) => useMemo(() => getOrdinalColorScale<Datum>(config, identity), [config, identity])
